import { Button } from "antd";
import { useEffect, useRef, useState } from 'react'
import './App.css'
import classNames from 'classnames'

function App() {
  const [ commentList, setCommentList ] = useState([])
  const[text,setText]=useState('')
  const[date,setDate]=useState('')
  const[acheck,setaCheck]=useState('false')
  const[flag,setFlag]=useState(false)
  let id=0

  const handlPublish=()=>{
    if(date!==''&&text!==''){
    setCommentList([
      ...commentList,{
        item:text,
        date:date,
        id:id,
        check:false
      }
    ])
    id++
    setFlag(true)
  }
  else{
    alert('输错了')
  }
  setText('')
  setDate('')
  }

  function Item ({ item, date,check}){
    const handlDel=()=>{}

    const changeCheck=()=>{
      if(check){
      check=false
      }
      else{
      check=true
      }
      setaCheck(true)
    }

    return(
      <div className="incident">
      <div className={classNames({in_font:check===false},{check_font:check===true})}>
        <p>{item}</p>
        <p>{date}</p>
        </div>
      <div className="left">
      <input type="checkbox" onChange={changeCheck}></input>
      <div onClick={handlDel} >del</div>
      </div>
    </div>
    )
}

  console.log(text);
  console.log(date);
  return (
    <div className="App">
      <h1 className='title'>To Do List</h1>
      <div className='input_block'>
      <input type='text' className='input_first' placeholder="输入任务名称" value={text} onChange={(e)=>setText(e.target.value)}></input>
      </div>


      <div className='input_block'>
      <input type="date" className='input_second' value={date} onChange={(e)=>setDate(e.target.value)}></input>
      <Button type="primary" className="button_add" style={{paddingRight:'40px'} } onClick={handlPublish}>添加</Button>
      </div>


      <div className={classNames('body',{body_active:flag===false})}>
      {commentList.map(item => <Item key={item.id} item={item.item} date={item.date} check={item.check}/>)}
      </div>

    </div>
  )
 }


export default App;
