import { Button } from "antd";
import { useEffect, useRef, useState } from 'react'
import './App.css'
import classNames from 'classnames'

function App() {
  const [ commentList, setCommentList ] = useState([])
  const[text,setText]=useState('')
  const[date,setDate]=useState('')
  const[check,setCheck]=useState('false')
  const[flag,setFlag]=useState(false)
  let id=0

  const handlPublish=()=>{
    if(date!==''&&text!==''){
    setCommentList([
      ...commentList,{
        item:text,
        date:date,
        id:id
      }
    ])
    id++
    setFlag(true)
  }
  else{
    alert('输错了')
  }
  setText('')
  setDate('')
  }

  function Item ({ item, date}){
    const checkRef=useRef(null)

    useEffect(() => {
      if (checkRef.current) {
        const isChecked = checkRef.current.checked;
        // 进行你的逻辑操作
        console.log('Checkbox checked:', isChecked);
      }
    }, [checkRef.current]);

    return(
      <div className="incident">
      <div className={classNames({in_font:checkRef.current &&checkRef.current.checked==false})}>
        <p>{item}</p>
        <p>{date}</p>
        </div>
      <div className="left">
      <input type="checkbox" ref={checkRef}></input>
      <div>del</div>
      </div>
    </div>
    )
  }

  console.log(text);
  console.log(date);
  return (
    <div className="App">
      <h1 className='title'>To Do List</h1>
      <div className='input_block'>
      <input type='text' className='input_first' placeholder="输入任务名称" value={text} onChange={(e)=>setText(e.target.value)}></input>
      </div>


      <div className='input_block'>
      <input type="date" className='input_second' value={date} onChange={(e)=>setDate(e.target.value)}></input>
      <Button type="primary" className="button_add" style={{paddingRight:'40px'} } onClick={handlPublish}>添加</Button>
      </div>


      <div className={classNames('body',{body_active:flag===false})}>
      {commentList.map(item => <Item key={item.id} item={item.item} date={item.date}/>)}
      </div>

    </div>
  )
}

export default App;
